EntityService.script:
var employee_id/*:Number*/ = param.get("employee_id");
if(employee_id == null )
	throw ("未查询到相应的职员ID，请检查。");
var hql/*:String*/ = "update Employee set isBlocked = 0";
hql += ", errorCount = 0, lastErrorTime = null where id=:id ";

var query/*:ScriptQuery*/ = service.createHQLQuery(hql);

query.setLong("id",Number(employee_id));

query.executeUpdate();

formEvent.event:
var entity = form.getEntity();
var enabled = entity.isLoginEnabled;
if( enabled == true )
  enabled = true;
else
  enabled = false;
form.setFieldProperty("loginId", "enabled", enabled);

var entity/*:$Entity.Employee*/ = form.getEntity();
if( form.getFormState() == "自我编辑" )
	return;

var enabled = entity.isLoginEnabled;
if( enabled == true )
  enabled = true;
else
  enabled = false;
form.setFieldProperty("loginId", "enabled", enabled);

queryEvent.event:
var entities = query.getSelectedEntities("Employee");
if(entities.length == 0)
	return {error:true, message:"请选择记录"};
var entity/*:$Entity.Employee*/ = entities[0];
if(entity.isBlocked == null || entity.isBlocked == false){
	return {error:true, message:"此用户未锁定，无需解锁。"};
}
var param = {};
param.employee_id = entity.id;
query.fireRemoteEvent("Employee.onUnlock", param, "ck_onUnlock");
